	(21,'Tipo de estabilidad que trabaja alrededor del Eje Vertical.',1,'Lateral.','Longitudinal.','Direccional.',NULL,NULL,'C',NULL,15,NULL,NULL,NULL,NULL),
	(22,'Todo cuerpo permanece en su estado de reposo o de movimiento rectilíneo uniforma a velocidad constante, mientras no actue ninguna fuerza sobre el:',1,'Ley de Inercia.','Ley de acción-reacción.','Ley de Boyle.',NULL,NULL,'A',NULL,15,NULL,NULL,NULL,NULL),
